-- Clear existing Social Studies questions for idempotency (Variants 1, 2, 3, 4)
DO $$
DECLARE
    subj_soc_id INT;
BEGIN
    SELECT id INTO subj_soc_id FROM public.subjects WHERE name = '–û–±—â–µ—Å—Ç–≤–æ–∑–Ω–∞–Ω–∏–µ';

    IF subj_soc_id IS NOT NULL THEN
