-- Clear existing Social Studies questions for idempotency (Variants 5, 6, 7, 8, 9, 10)
DO $$
DECLARE
    subj_soc_id INT;
BEGIN
    SELECT id INTO subj_soc_id FROM public.subjects WHERE name = '–û–±—â–µ—Å—Ç–≤–æ–∑–Ω–∞–Ω–∏–µ' AND grade_level = 6;

    IF subj_soc_id IS NOT NULL THEN
        RAISE NOTICE 'Deleting existing data for –û–±—â–µ—Å—Ç–≤–æ–∑–Ω–∞–Ω–∏–µ Variants 5, 6, 7, 8, 9, 10...';
        DELETE FROM public.vpr_answers a
        USING public.vpr_questions q
        WHERE a.question_id = q.id
          AND q.subject_id = subj_soc_id
          AND q.variant_number IN (5, 6, 7, 8, 9, 10);

        DELETE FROM public.vpr_questions
        WHERE subject_id = subj_soc_id
          AND variant_number IN (5, 6, 7, 8, 9, 10);
        RAISE NOTICE 'Deletion complete for –û–±—â–µ—Å—Ç–≤–æ–∑–Ω–∞–Ω–∏–µ Variants 5, 6, 7, 8, 9, 10.';
    ELSE
        RAISE NOTICE 'Subject "–û–±—â–µ—Å—Ç–≤–æ–∑–Ω–∞–Ω–∏–µ" not found. Skipping deletion.';
    END IF;
END $$;


